// ** Глобальні змінні для елементів DOM **
let backToTopBtn;
let videoSection; // Секція відео
let lyricsSection; // Секція текстів пісень

// Функція для відображення випадаючого списку
function showDropdown() {
  const dropdown = document.getElementById("dropdown");
  dropdown.style.display = "block";
  updateDropdown("");
}

// Функція для оновлення списку при введенні
function filterCountries() {
  const searchValue = document
    .getElementById("country-search")
    .value.toLowerCase();
  updateDropdown(searchValue);
}

// Функція для заповнення випадаючого списку
function updateDropdown(searchValue) {
  const dropdown = document.getElementById("dropdown");
  dropdown.innerHTML = "";

  const specialEntries = [
    "Grand final",
    "First Semi-Final",
    "Second Semi-Final",
  ];

  specialEntries.forEach((entry) => {
    if (entry.toLowerCase().includes(searchValue)) {
      const div = document.createElement("div");
      const img = document.createElement("img");
      img.src = countryData[entry].flag;
      div.appendChild(img);

      const text = document.createTextNode(entry);
      div.appendChild(text);

      div.onclick = () => {
        document.getElementById("country-search").value = entry;
        dropdown.style.display = "none";
        changeContent(entry);
      };
      dropdown.appendChild(div);
    }
  });

  Object.keys(countryData)
    .sort()
    .forEach((country) => {
      if (specialEntries.includes(country)) {
        return;
      }

      if (country.toLowerCase().includes(searchValue)) {
        const div = document.createElement("div");
        const img = document.createElement("img");
        img.src = countryData[country].flag;
        div.appendChild(img);

        const text = document.createTextNode(country);
        div.appendChild(text);

        div.onclick = () => {
          document.getElementById("country-search").value = country;
          dropdown.style.display = "none";
          changeContent(country);
        };
        dropdown.appendChild(div);
      }
    });
}

// Функція зміни контенту
function changeContent(country) {
  if (!countryData[country]) return;

  const singerName = document.getElementById("singer-name");
  const singerInfo = document.getElementById("singer-info");
  const songElement = document.getElementById("song");
  const songInfoElement = document.getElementById("song-info");
  const originalLyrics = document.getElementById("original-lyrics");
  const translatedLyrics = document.getElementById("translated-lyrics");
  const infoSection = document.querySelector(".info-section");

  // Скидаємо видимість елементів до "стану за замовчуванням", щоб вони могли бути показані, якщо потрібно
  singerName.style.display = "";
  singerInfo.style.display = "";
  infoSection.style.display = "";
  lyricsSection.style.display = ""; // Використовуємо lyricsSection, що вже отриманий

  // Очищаємо вміст перед заповненням новим
  singerName.textContent = "";
  singerInfo.textContent = "";
  songElement.textContent = "";
  songInfoElement.textContent = "";
  originalLyrics.textContent = "";
  translatedLyrics.textContent = "";

  // Отримуємо посилання на елементи H3 всередині lyrics-column, щоб керувати їх текстом
  const originalLyricsH3 = originalLyrics.previousElementSibling;
  const translatedLyricsH3 = translatedLyrics.previousElementSibling;

  if (
    country === "Grand final" ||
    country === "First Semi-Final" ||
    country === "Second Semi-Final"
  ) {
    document.getElementById("video-frame").src = countryData[country].video;
    singerName.textContent = countryData[country].singer;
    songElement.textContent = "Про конкурс:";
    songInfoElement.textContent = countryData[country].songinfo;

    singerInfo.style.display = "none"; // Приховуємо деталі про співака
    lyricsSection.style.display = "none"; // Приховуємо тексти пісень

    if (originalLyricsH3) originalLyricsH3.textContent = "";
    if (translatedLyricsH3) translatedLyricsH3.textContent = "";
  } else {
    document.getElementById("video-frame").src = countryData[country].video;
    singerName.textContent = "Виконавець: " + countryData[country].singer;
    singerInfo.textContent =
      "Інформація про виконавця: " + countryData[country].singerinfo;
    songElement.textContent = "Про що пісня: " + countryData[country].song;
    songInfoElement.textContent = countryData[country].songinfo;
    originalLyrics.textContent = countryData[country].originalLyrics;
    translatedLyrics.textContent = countryData[country].translatedLyrics;

    if (originalLyricsH3) originalLyricsH3.textContent = "Оригінальний текст:";
    if (translatedLyricsH3) translatedLyricsH3.textContent = "Переклад:";
  }

  // Після зміни контенту завжди перевіряємо стан кнопки "До відео"
  toggleBackToTopButton();
}

// Функція, яка перевіряє позицію прокрутки та показує/ховає кнопку
function toggleBackToTopButton() {
  // Перевірка, чи всі елементи існують перед використанням
  if (!backToTopBtn || !videoSection) return;

  // Отримати позицію елемента відео відносно верхнього краю вікна перегляду
  const videoRect = videoSection.getBoundingClientRect();

  // Якщо відео-секція (або її верх) вже за межами екрану ВГОРУ (тобто videoRect.top < 0)
  // АБО якщо користувач прокрутив сторінку вниз значно (наприклад, більше 200px)
  // Це забезпечує, що кнопка з'являється, коли відео не видно, і користувач прокрутив вниз
  if (videoRect.bottom < 0 || window.scrollY > 200) {
    backToTopBtn.classList.add("show");
  } else {
    backToTopBtn.classList.remove("show");
  }
}

// ** ОДИН ЄДИНИЙ document.addEventListener("DOMContentLoaded") **
document.addEventListener("DOMContentLoaded", () => {
  // Ініціалізація DOM-елементів після завантаження сторінки
  backToTopBtn = document.getElementById("backToTopBtn");
  videoSection = document.getElementById("video-section");
  lyricsSection = document.getElementById("lyrics-section"); // Отримуємо посилання на секцію текстів

  // Заповнюємо список країн при першому завантаженні
  updateDropdown("");
  // Обираємо "Grand final" за замовчуванням при завантаженні сторінки
  changeContent("Grand final");

  // Додаємо обробник подій прокрутки вікна
  window.addEventListener("scroll", toggleBackToTopButton);

  // Ініціалізуємо стан кнопки при завантаженні сторінки
  toggleBackToTopButton(); // Важливо викликати один раз при завантаженні

  // Приховати dropdown при кліку поза ним
  document.addEventListener("click", (event) => {
    const dropdown = document.getElementById("dropdown");
    const countrySearch = document.getElementById("country-search");
    if (!dropdown.contains(event.target) && event.target !== countrySearch) {
      dropdown.style.display = "none";
    }
  });

  // Логіка для кнопки-гамбургера (якщо є, переконайтеся, що елементи існують у HTML)
  const menuToggle = document.querySelector(".menu-toggle");
  const headerDropdownList = document.querySelector(".countries-list");

  if (menuToggle && headerDropdownList) {
    menuToggle.addEventListener("click", () => {
      headerDropdownList.classList.toggle("active");
      menuToggle.classList.toggle("active");
    });

    // Закриваємо меню, якщо клікнули на країну (для мобільних)
    const dropdown = document.getElementById("dropdown");
    dropdown.addEventListener("click", (event) => {
      if (
        event.target.tagName === "DIV" &&
        event.target.closest(".dropdown div")
      ) {
        if (headerDropdownList.classList.contains("active")) {
          headerDropdownList.classList.remove("active");
          menuToggle.classList.remove("active");
        }
      }
    });
  }
});
